import useStyles from './styles';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useState } from 'react';
import FileBase from 'react-file-base64';

const Form = () => {
    const [postData, setPostData] = useState({
        creator: '',
        title: '',
        message: '',
        tags: '',
        selectedFile: ''
    });

    const formStyles = useStyles();

    const handleSubmit = () => {

    };

    const clear = () => {

    };

    return (
        <Paper className={formStyles.paper}>
            <form autoComplete="off" 
            noValidate 
            className={`${formStyles.form} ${formStyles.root}`} 
            onSubmit={handleSubmit}>
                <Typography variant="h6">Creating a Memory</Typography>
                <TextField 
                name="creator" 
                variant="outlined" 
                label="Creator" 
                fullWidth
                /* data from post will be stored in the state object postData, 
                and each object key will be a specific text field */
                value={postData.creator}
                onChange={(e) => setPostData({ ...postData, creator: e.target.value })} />
                {/* We need to use spread to keep other properties of state postData intact,
                otherwise the other text fields will disappear and only creator will remain 
                Now repeat below for the other fields */}
                <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e) => ({ ...postData, title: e.target.value })} />
                <TextField name="message" variant="outlined" label="Message" fullWidth value={postData.message} onChange={(e) => ({ ...postData, message: e.target.value })} />
                <TextField name="tags" variant="outlined" label="Tags" fullWidth value={postData.tags} onChange={(e) => ({ ...postData, tags: e.target.value })} />
                <div className={formStyles.fileInput}>
                    <FileBase
                        type="file"
                        multiple={false}
                        // destructuring base64
                        onDone={({base64}) => setPostData({ ...postData, selectedFile: base64})} 
                    />
                </div>
                <Button className={formStyles.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
                <Button variant="contained" color="secondary" size="small" onChange={clear} fullWidth>Clear</Button>
            </form>
        </Paper>
    );
};

export default Form;
