import { FETCH_ALL, CREATE } from "../actions/types";

const initialState = {
    posts: [],
};

const postsReducer = (state=initialState, action) => {
    switch(action.type) {
        case FETCH_ALL:
            return {
                ...state,
                posts: action.payload
            };
        case CREATE:
            return state.posts;
        default: 
            return state.posts;
    };
};

export default postsReducer;
