import { AUTH, LOGOUT } from '../actions/types';

const initialState = {
    authData: null
}
// get user data
const authReducer = (state=initialState, action) => {
    switch(action.type) {
        case AUTH:
            localStorage.setItem('profile', { ...action?.payload });
            return {
                ...state,
                authData: action?.payload
            };
        case LOGOUT:
            console.log("logout not yet implemented");
            return state;
        default:
            return state;
    }
}

export default authReducer;
