import useStyles from './styles'
import { Avatar, Button, Paper, Grid, Typography, Container } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { mergeClasses } from '@material-ui/styles';
import Input from './Input';
import { useState } from 'react';

function Auth() {
    const authStyles = useStyles();

    const [isSignup, setIsSignup] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    const handleShowPassword = () => {
        // toggle state of showPassword
        setShowPassword(!showPassword);
    };

    const switchMode = () => {
        setIsSignup(!isSignup);
        // reset show password
        handleShowPassword();
    };

    const handleSubmit = () => {

    };

    const handleChange = () => {

    };

    return (
        <Container component="main" maxWidth="xs">
            <Paper className={authStyles.paper} elevation={3}>
                <Avatar className={authStyles.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
            <Typography variant="h5">{isSignup ? 'Sign Up' : 'Sign In'}</Typography>
            <form className={authStyles.form} onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    { isSignup && ( // ask for first and last name if signing up
                        <>
                            <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus="true" half="true" />
                            <Input name="Last Name" label="Last Name" handleChange={handleChange} half="true" />
                        </>
                    )}
                    <Input name="email" label="Email Address" handleChange={handleChange} type="email" />
                    <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword} />
                    {/* If this is signing up, then we need a confirm password field */}
                    { isSignup && <Input name="confirmPassword" label="repeat Password" handleChange={handleChange} />}
                </Grid>
                <Button type="submit" fullwidth="true" variant="contained" color="primary" className={authStyles.submit}>
                    {isSignup ? "Sign Up" : "Log In"}
                </Button>
                <Grid container justifyContent="flex-end">
                    <Grid item>
                        <Button onClick={switchMode}>
                            {isSignup ? 'Already have an account? Sign In' : 'Don\' have an account? Sign Up'}
                        </Button>
                    </Grid>
                </Grid>
            </form>
            </Paper>
        </Container>
    )
}

export default Auth
