import { AUTH, LOGOUT } from '../actions/types';

// get user data
const authReducer = (state, action) => {
    switch(action.type) {
        case AUTH:
            console.log(action?.data);
            return state;
        case LOGOUT:
            console.log("logout not yet implemented");
            return state;
        default:
            return state;
    }
}

export default authReducer;
