import { AUTH, LOGOUT } from '../actions';

// get user data
const authReducer = (state, action) => {
    switch(action.type) {
        case AUTH:
            console.log(action?.data);
        case LOGOUT:
            console.log("logout not yet implemented");
        default:
            break;
    }
}

export default authReducer;
